sp_widget.client_script:
api.controller = function($scope, $window, spUtil) {
  var c = this;

  c.goToShop = function() {
    $window.location.href = '/psmp?id=psmp_shop';
  };

  c.viewDetails = function(order) {
    $window.location.href = '/psmp?id=psmp_order_details&sys_id=' + order.sys_id;
  };

  // Simple color mapping for states
  c.getStateClass = function(state) {
    switch (state.toLowerCase()) {
      case 'pending': return 'state-pending';
      case 'in progress': return 'state-progress';
      case 'completed': return 'state-complete';
      case 'rejected': return 'state-rejected';
      default: return '';
    }
  };
};

